    entity2color(val) {
        switch (val) {
            case 'S-ANG':
			case 'B-ANG':
			case 'I-ANG':
			case 'E-ANG':
                return "orange";
                break;
			case 'S-DUC':
			case 'B-DUC':
			case 'I-DUC':
			case 'E-DUC':
                return "red";
                break;
			case 'S-EVE':
			case 'B-EVE':
			case 'I-EVE':
			case 'E-EVE':
                return "blue";
                break;
			case 'S-FAC':
			case 'B-FAC':
			case 'I-FAC':
			case 'E-FAC':
                return "brown";
                break;
			case 'S-GPE':
			case 'B-GPE':
			case 'E-GPE':
			case 'I-GPE':
                return "purple";
                break;
			case 'S-LOC':
			case 'B-LOC':
			case 'E-LOC':
			case 'I-LOC':
                return "yellow";
                break;
			case 'S-ORG':
			case 'B-ORG':
			case 'E-ORG':
			case 'I-ORG':
                return "green";
                break;
			case 'S-PER':
			case 'B-PER':
			case 'E-PER':
			case 'I-PER':
                return "#f1b507";
                break;
			case 'S-WOA':
			case 'B-WOA':
			case 'I-WOA':
			case 'E-WOA':
                return "#07bcf1";
                break;
            case 'O':
			case 'PAD':
                return "";
                break;
            default:
                return 'white';
        }
    }
